<?phpif(!class_exists('model')){    class Model    {        private $con;        function __construct()        {            $this->con = $this->connect();        }        function connect()        {            $con = new mysqli(HOST, USER, PASS, DB);            $con->set_charset('utf8');            if ($con->connect_errno > 0)                die('DB Error - connect [' . $this->con->connect_error . ']');            return $con;        }        function consult($query)        {            if ($this->con->query($query))                return $this->con->affected_rows;//            die('DB Error - consult [' . $query . ']');        }        function consult_id($query)        {            if ($this->con->query($query))                return mysqli_insert_id($this->con);//            else//                die('DB Error - consultId [' . $query . ']');        }        function consult_array($query,$field=null)        {            if (empty($query))                return false;            $res = array();            if ($sql = $this->con->query($query)) {                while ($results = $sql->fetch_assoc()) {                    if ($field && !empty($results))                        $res[] = $results[$field];                    else                        $res[] = $results;                }                return ($res);            }//            die('DB Error - consulta_array [' . $query . ']');        }    }}?>