INSERT INTO `comics` (`comicsId`, `comicsName`, `comicsExtension`, `comicsNumber`, `comicsPrice`, `comicsBuy`, `comicsDescription`, `comicsUrl`) VALUES
	(22506, 'Avengers: The Initiative (2007) #19', 'jpg', 19, '2.99', 'http://comicstore.marvel.com/Avengers-The-Initiative-19/digital-comic/10949?utm_campaign=apiRef&utm_source=3e34603d82ff4d468843e222ddb951b2', 'Join 3-D MAN, CLOUD 9, KOMODO, HARDBALL, and heroes around America in the battle that will decide the fate of the planet and the future of the Initiative program. Will the Kill Krew Army win the day?', 'http://gateway.marvel.com/v1/public/comics/22506'),
	(22300, 'Avengers: The Initiative (2007) #18 (ZOMBIE VARIANT)', 'jpg', 18, '2.99', '', 'SECRET INVASION TIE-IN!\r<br>THE EXPLOSIVE FINALE STARTS HERE!\r<br>Now that the KILL KREW knows Skrullowjacket\'s master plan, can they stop the TRUE purpose of the Fifty State Initiative? Plus:  It\'s THOR GIRL vs. ULTRA GIRL!  One is more than she appears to be... and the other\'s a Skrull. And, after fourteen issues, are we REALLY going to unmask MUTANT ZERO?!\r<br>Rated T+ ...$2.99\r<br>', 'http://gateway.marvel.com/v1/public/comics/22300'),
	(22299, 'Avengers: The Initiative (2007) #18', 'jpg', 18, '2.99', 'http://comicstore.marvel.com/Avengers-The-Initiative-18/digital-comic/10948?utm_campaign=apiRef&utm_source=3e34603d82ff4d468843e222ddb951b2', 'Now that the Kill Krew knows Skrullowjacket\'s master plan, can they stop the true mission of the Fifty State Initiative? Plus, Thor Girl vs. Ultra Girl! One is more than she appears to be and the other\'s a Skrull!', 'http://gateway.marvel.com/v1/public/comics/22299'),
	(21975, 'Avengers: The Initiative (2007) #17', 'jpg', 17, '2.99', 'http://comicstore.marvel.com/Avengers-The-Initiative-17/digital-comic/10721?utm_campaign=apiRef&utm_source=3e34603d82ff4d468843e222ddb951b2', 'Join Mutant Zero, Trauma, Bengal, Constrictor, and Ant-Man as they undertake this war\'s most dangerous mission: to take out the Skrull Spider-Woman! Plus, a new Avenger and more clues as to Mutant Zero\'s identity!', 'http://gateway.marvel.com/v1/public/comics/21975'),
	(21741, 'Avengers: The Initiative (2007) #16', 'jpg', 16, '2.99', 'http://comicstore.marvel.com/Avengers-The-Initiative-16/digital-comic/10718?utm_campaign=apiRef&utm_source=3e34603d82ff4d468843e222ddb951b2', 'The Skrull Kill Krew is back! And they\'re ready to kill, maim, torture, and butcher every mother lovin\' Skrull out there! Also, back at Camp Hammond, Ant-Man is in a giant world of trouble.', 'http://gateway.marvel.com/v1/public/comics/21741'),
	(21546, 'Avengers: The Initiative (2007) #15', 'jpg', 15, '2.99', 'http://comicstore.marvel.com/Avengers-The-Initiative-15/digital-comic/10716?utm_campaign=apiRef&utm_source=3e34603d82ff4d468843e222ddb951b2', 'It\'s up to the Initiative to face off against the first major Skrull strike force! And on the battle field, the cadet with the biggest secret has to choose which side he\'s really on. Plus, a new low for Ant-Man and a new danger for War Machine!', 'http://gateway.marvel.com/v1/public/comics/21546'),
	(21366, 'Avengers: The Initiative (2007) #14', 'jpg', 14, '2.99', 'http://comicstore.marvel.com/Avengers-The-Initiative-14/digital-comic/10715?utm_campaign=apiRef&utm_source=3e34603d82ff4d468843e222ddb951b2', 'The fates of The Initiative, the United States, and Planet Earth hang in the balance. Plus: Former Avenger, Delroy Garret, assumes the mantle and arsenal of Earth\'s greatest Skrull-Hunter, The 3-D Man.', 'http://gateway.marvel.com/v1/public/comics/21366'),
	(24571, 'Avengers: The Initiative (2007) #14 (SPOTLIGHT VARIANT)', 'jpg', 14, '2.99', '', '', 'http://gateway.marvel.com/v1/public/comics/24571'),
